<node CREATED="1345114645013" ID="ID_499109861" MODIFIED="1345207472174" TEXT="Define what reqy is">
<node CREATED="1345114655130" ID="ID_1271625980" MODIFIED="1345114661957" TEXT="Don&apos;t compete on features"/>
<node CREATED="1345114662356" ID="ID_311759846" MODIFIED="1345114667066" TEXT="Compete on experience?"/>
<node CREATED="1345114695797" ID="ID_1886601153" MODIFIED="1345114705484" TEXT="Reqy is lightweight requirements management"/>
